using System.Collections.Generic;
using System.Linq;
using Microsoft.EntityFrameworkCore;
using StackExchange.Redis;

public class UserRepository
{
    private readonly RedisCacheClient _redisClient;
    private readonly UserDbContext _dbContext;

    public UserRepository(RedisCacheClient redisClient, UserDbContext dbContext)
    {
        _redisClient = redisClient;
        _dbContext = dbContext;
    }

    public void AddUser(User user)
    {
        _dbContext.Users.Add(user);
        _dbContext.SaveChanges();

        _redisClient.Db.HashSet(user.Id.ToString(), user.ToHashEntries());
    }

    public void UpdateUser(User user)
    {
        _dbContext.Users.Update(user);
        _dbContext.SaveChanges();

        _redisClient.Db.HashSet(user.Id.ToString(), user.ToHashEntries());
    }

    public void DeleteUser(int userId)
    {
        var user = _dbContext.Users.Find(userId);

        if (user != null)
        {
            _dbContext.Users.Remove(user);
            _dbContext.SaveChanges();

            _redisClient.Db.KeyDelete(userId.ToString());
        }
    }

    public User GetUser(int userId)
    {
        var user = _redisClient.Db.HashGetAll(userId.ToString());

        if (user.Length == 0)
        {
            return _dbContext.Users.Find(userId);
        }

        return user.ToObject<User>();
    }

    public List<User> GetUsers()
    {
        var users = _dbContext.Users.ToList();

        foreach (var user in _redisClient.Db.HashGetAll("users"))
        {
            var id = int.Parse(user.Name);
            var hashEntries = user.Value.ToString().ToHashEntries();
            var redisUser = hashEntries.ToObject<User>();
            redisUser.Id = id;
            users.Add(redisUser);
        }

        return users;
    }

    public List<User> GetUsersByName(string name)
    {
        var users = _dbContext.Users.Where(u => u.Name == name).ToList();

        var redisUsers = _redisClient.Db.HashScan("users", $"*\"Name\":\"{name}\"*");

        foreach (var redisUser in redisUsers)
        {
            var id = int.Parse(redisUser.Name);
            var hashEntries = redisUser.Value.ToString().ToHashEntries();
            var user = hashEntries.ToObject<User>();
            user.Id = id;
            users.Add(user);
        }

        return users;
    }

    public List<User> GetUsersByEmail(string email)
    {
        var users = _dbContext.Users.Where(u => u.Email == email).ToList();

        var redisUsers = _redisClient.Db.HashScan("users", $"*\"Email\":\"{email}\"*");

        foreach (var redisUser in redisUsers)
        {
            var id = int.Parse(redisUser.Name);
            var hashEntries = redisUser.Value.ToString().ToHashEntries();
            var user = hashEntries.ToObject<User>();
            user.Id = id;
            users.Add(user);
        }

        return users;
    }

    public List<User> GetUsersByNameAndEmail(string name, string email)
    {
        var users = _dbContext.Users.Where(u => u.Name == name && u.Email == email).ToList();

        var redisUsers = _redisClient.Db.HashScan("users", $"*\"Name\":\"{name}\",\"Email\":\"{email}\"*");

