    // Parsing functions for control structures
    // ...

    // Parsing functions for function and class definitions
    // ...

    // Parsing functions for function calls
    // ...

    // Parsing functions for modules and imports
    // ...

    pub fn parse_program(&mut self) -> ParseResult<Program> {
        let mut program = vec![];
        while let Some(_) = self.peek_char() {
            let stmt = self.parse_statement()?;
            program.push(stmt);
        }
        Ok(program)
    }
}

pub fn parse(input: &str) -> ParseResult<Program> {
    let mut parser = Parser::new(input);
    parser.parse_program()
