Sure! Here's an implementation of the custom exporter and processor that you described:
```go
package main

import (
	"context"
	"encoding/json"
	"fmt"
	"net/http"

	"go.opentelemetry.io/otel/sdk/export/trace"
)

type jsonExporter struct {
	client http.Client
	url    string
}

func newJSONExporter(url string) *jsonExporter {
	return &jsonExporter{
		client: http.Client{},
		url:    url,
	}
}

func (e *jsonExporter) ExportSpans(ctx context.Context, sd []*trace.SpanData) error {
	// Filter out spans with library.name != "ticks"
	filteredSpans := make([]*trace.SpanData, 0, len(sd))
	for _, span := range sd {
		if span.Attributes.Value("library.name") == "ticks" {
			filteredSpans = append(filteredSpans, span)
		}
	}

	// Marshal the filtered spans to JSON
	jsonBytes, err := json.Marshal(filteredSpans)
	if err != nil {
		return fmt.Errorf("failed to marshal spans to JSON: %w", err)
	}

	// Send the JSON data to the HTTP endpoint
	req, err := http.NewRequestWithContext(ctx, "POST", e.url, jsonBytes)
	if err != nil {
		return fmt.Errorf("failed to create HTTP request: %w", err)
	}
	req.Header.Set("Content-Type", "application/json")
	resp, err := e.client.Do(req)
	if err != nil {
		return fmt.Errorf("failed to send HTTP request: %w", err)
	}
	defer resp.Body.Close()
	if resp.StatusCode != http.StatusOK {
		return fmt.Errorf("server returned non-200 status code: %d", resp.StatusCode)
	}

	return nil
}

type filterProcessor struct{}

func (p *filterProcessor) Process(ctx context.Context, td trace.TraceData) (trace.TraceData, error) {
	// Filter out spans with library.name != "ticks"
	filteredSpans := make([]*trace.SpanData, 0, len(td.Spans))
	for _, span := range td.Spans {
		if span.Attributes.Value("library.name") == "ticks" {
			filteredSpans = append(filteredSpans, span)
		}
	}
	td.Spans = filteredSpans

	return td, nil
}
```
