    }

    // Check for the opponent's winning move and block it
    for (int i = 0; i < BOARD_SIZE; i++) {
        for (int j = 0; j < BOARD_SIZE; j++) {
            if (tempBoard[i][j] == EMPTY) {
                tempBoard[i][j] = PLAYER1;
                if (GetWinningDirection(PLAYER1)) {
                    return (Vector2){j, i};
                }
                tempBoard[i][j] = EMPTY;
            }
        }
    }

    // If neither of the above, make a random move
    Vector2 moves[BOARD_SIZE * BOARD_SIZE];
    int moveCount = 0;

    for (int i = 0; i < BOARD_SIZE; i++) {
        for (int j = 0; j < BOARD_SIZE; j++) {
            if (board[i][j] == EMPTY) {
                moves[moveCount++] = (Vector2){j, i};
            }
        }
    }

    if (moveCount > 0) {
        bestMove = moves[GetRandomValue(0, moveCount - 1)];
    }

    return bestMove;
}
```
