```javascript
import React, { useState } from "react";

interface Register {
  name?: string;
  email?: string;
  token?: string;
}

const page = () => {
  const [name, setName] = useState<string>("");
  const [email, setEmail] = useState<string>("");
  const [token, setToken] = useState<string | null>(null);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    try {
      const response = await fetch(`/api/auth`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ clientName: name, clientEmail: email }),
      });

      if (!response.ok) {
        throw new Error("An error occurred while fetching the book details");
      }

      const data: Register = await response.json();
      setToken(data.token);
      setErrorMessage(null);
    } catch (error) {
      setToken(null);
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="flex flex-col mt-12 space-y-4 max-w-2xl w-full mx-auto">
      <div className="text-center">
        <h2 className="text-3xl font-semibold">Get Any Book Details</h2>
        <p className="text-lg mt-2">Enter the Book ID to get the Book Details</p>
      </div>
      <div className="w-4/6 mx-auto">
        <form onSubmit={handleSubmit} className="flex flex-col">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            name="name"
            placeholder={"Enter Your Name"}
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="border border-white rounded-md py-2 px-2 text-gray-800"
          />

          <label htmlFor="email">Email</label>
          <input
            type="email"
            name="email"
            placeholder={"Enter Your Email"}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="border border-white rounded-md py-2 px-2 text-gray-800"
          />

          <button
            type="submit"
            className="w-2/4 py-1 px-3 rounded-md mx-auto mt-4 bg-neutral-700 border border-white"
          >
            Fetch Book
          </button>
        </form>
      </div>
      <div className="w-full mx-auto text-center">
        {errorMessage && <div>Error: {errorMessage}</div>}
        {token && (
          <div>
            <p>{token}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default page;
```
