import '../App.css';

class Chat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [],
      input: ''
    };
  }

  componentDidMount() {
    this.updateMessages();
  }

  sendMessage = () => {
    axios.post('/messages', {
      message: this.state.input
    })
      .then(response => {
        this.updateMessages();
        this.setState({ input: '' });
      })
      .catch(error => {
        console.log(error);
      });
  }

  updateMessages = () => {
    axios.get('/messages')
      .then(response => {
        this.setState({ messages: response.data });
      })
      .catch(error => {
        console.log(error);
      });
  }

  handleInputChange = (event) => {
    this.setState({ input: event.target.value });
  }

  render() {
