var (
 moduels\_desc = make([]ModulesDesc, 0)
 moduels\_desc\_lock sync.RWMutex
)

//通过插件来注册表模型

type ModulesDesc struct {
 obj interface{}
 md reflect.Value
 desc TableDesc
 schema string
}

func (s \*ModulesDesc) Obj() interface{} {
 return s.obj
}

func (s \*ModulesDesc) Desc() \*TableDesc {
 return &s.desc
}

func (s \*ModulesDesc) Md() reflect.Value {
 return s.md
}

func (s \*ModulesDesc) Schema() string {
 return s.schema
}

type DynamicModelOpt struct {
 Schema string //一个模型对多个 表 SCHEMA 的时候 指定 SCHEMA 别名
}

//当同一个模型，譬如 KV，而且对应的表有多个不同的真实 SCHEMA ，则 是 一对多的 SCHEMA
//其需要实现 模型到 SCHEMA 的 转换接口
type OrmModelSchema interface {
 TableSchema() string //一个模型，多个不同的表结构。需要实现该接口来注册
}

func RegisterModuleByDesc(md interface{}, desc TableDesc) error {
 mdesc := ModulesDesc{}
 mdesc.obj = md
 mdesc.desc = desc
 mdesc.md = reflect.ValueOf(md)
 if toSchema, ok := md.(OrmModelSchema); ok {
 mdesc.schema = toSchema.TableSchema()
 }
 moduels\_desc\_lock.Lock()
 moduels\_desc = append(moduels\_desc, mdesc)
 moduels\_desc\_lock.Unlock()
 return nil
}

func GetRegisterdModulesLen() int {
 moduels\_desc\_lock.RLock()
 defer moduels\_desc\_lock.RUnlock()
 return len(moduels\_desc)
}

func GetRegisterdModules() []ModulesDesc {
 moduels\_desc\_lock.RLock()
 mds := make([]ModulesDesc, 0, len(moduels\_desc))
 for \_, v := range moduels\_desc {
 mds = append(mds, v)
 }
 moduels\_desc\_lock.RUnlock()
 return mds
}

